import React, { useState } from "react";
import './Calculator.css';
import './index.css';

const Calculator = () => {
	const [res, setRes] = useState(0)
	const [first, setFirst] = useState(null)
	const [second, setSecond] = useState(null)
	const [action, setAction] = useState('')

	const handleNumClicked = number => {
		if (!first) {
			setFirst(number)
		} else {
			setSecond(number)
		}
	}

	const handleEqClicked = () => {
		switch (action) {
			case '+': {
				setRes(first + second)
				break
			}
			case '-': {
				setRes(first - second)
				break
			}
			case 'x': {
				setRes(first * second)
				break
			}
			case '÷': {
				setRes(first / second)
				break
			}
			case 'c': {
				setRes(first / second)
				setFirst(null)
				setSecond(null)
				break
			}
			case 'ac': {
				setRes(first / second)
				setFirst(null)
				setSecond(null)
				break
			}
			case '=': {
				setRes(first / second)
				setFirst(null)
				setSecond(null)
				break
			}
		}
	}

	const handleActionClicked = () => {

	}

  return (
    <div className="container">
      <table>
        <tbody>
          <tr>
            <td onClick={handleEqClicked} colSpan="4" className="sum">
							{res}
						</td>
          </tr>
          <tr>
            <td onClick={() => handleActionClicked('mr')}>MR</td>
            <td onClick={() => handleActionClicked('mc')}>MC</td>
            <td onClick={() => handleActionClicked('m+')}>M+</td>
            <td onClick={() => handleActionClicked('m-')}>M-</td>
          </tr>
          <tr>
            <td onClick={() => handleActionClicked('ac')}>AC</td>
            <td onClick={() => handleActionClicked('c')} className="c">C</td>
            <td onClick={() => handleNumClicked(7)}>-/+</td>
            <td onClick={() => handleNumClicked('÷')}>÷</td>
          </tr>
          <tr>
            <td onClick={() => handleNumClicked(7)}>7</td>
            <td onClick={() => handleNumClicked(8)}>8</td>
            <td onClick={() => handleNumClicked(9)}>9</td>
            <td onClick={() => handleActionClicked('x')}>x</td>
          </tr>
          <tr>
            <td onClick={() => handleNumClicked(4)}>4</td>
            <td onClick={() => handleNumClicked(5)}>5</td>
            <td onClick={() => handleNumClicked(6)}>6</td>
            <td onClick={() => handleActionClicked('-')}>-</td>
          </tr>
          <tr>
            <td onClick={() => handleNumClicked(1)}>1</td>
            <td onClick={() => handleNumClicked(2)}>2</td>
            <td onClick={() => handleNumClicked(3)}>3</td>
            <td onClick={() => handleActionClicked('+')}>+</td>
          </tr>
          <tr>
            <td onClick={() => handleNumClicked(0)}>0</td>
            <td onClick={() => handleActionClicked(7)}>.</td>
            <td onClick={() => handleActionClicked('=')} colSpan="2" className="eq">=</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default Calculator;
